  console.log('current router', window.location.hash)
)
H5路由
history.pushState(name, title, toPath) // 入栈
history.replaceState(name, title, toPath) // 不会更改历史记录，替换当前状态值 
// 出栈时执行的回调函数
window.onpopstate = (e) => (
  console.log('h5 router change', e.state)
)
