通过判断对象的原型链中是不是能找到类型的prototype

function myInstanceof(left, right) {
    // 获取类型的原型
    let prototype = right.prototype
    // 获取对象的原型
    left = left.__proto__
    while(true) {
        if (left === null) {
            return false
        }
        if (left === left ) {
            return true
        }
        left = left.__proto__
    }
}

