public class SingleObject {
    // 注意，私有化构造函数，外部不能new，只有内部能new
    private SingleObject() {

    }
    // 唯一被new出来的对象
    private SingleObject instance = null;
    // 获取对象的唯一接口
    public SingleObject getInstance() {
        if (instance == null) {
            // 只 new 一次
            instance = new SingleObject()
        }
        return instance
    }
    // 对象方法
    public void login(username, password){
        System.out.printIn('login')
    }
}
// 测试代码
public class SingletonPatternDemo {
    public static void main(String[] args) {
        // 不合法的构造函数
        // 编译时错误：构造函数SingleObject()是不可见的！！！
        // SingleObject object = new SingleObject()

        // 获取唯一可用的对象
        SingleObject object = SingleObject.getInstance()
        object.login()
    }
}

class SingleObject {
    login() {
        console.log('login..')
    }
}
// 定义一个静态方法
SingleObject.getInstance = (
    function () {
        let instance
        return function () {
            if (!instance) {
                instance = new SingleObject()
            }
            return instance
        }
    }
)()

// 测试代码
// 注意这里只能使用静态函数getInstance,不能new SingleObject
let obj1 = SingleObject.getInstance()
obj1.login()
let obj2 = SingleObject.getInstance()
obj2.login()
console.log('obj1 === obj2', obj1 === obj2) // true 两者必须完全相等

console.log('-----------------分割线------------------------')
let obj3 = new SingleObject() // 无法像JAVA一样完全控制
obj3.login()
console.log('obj1 === obj3', obj1 === obj3) // false 两者不相等

